DROP TABLE IF EXISTS lmgc_Cities;
DROP TABLE IF EXISTS lmgc_Countries;
DROP TABLE IF EXISTS lmgc_Continents;

CREATE TABLE IF NOT EXISTS lmgc_Continents (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(100) NOT NULL UNIQUE,
    code VARCHAR(10) NOT NULL UNIQUE,
    surface_area DECIMAL(15, 2),
    population BIGINT,
    num_countries INT,
    description TEXT,
    notes TEXT,
    name_italian VARCHAR(100) UNIQUE,
    name_english VARCHAR(100) UNIQUE,
    name_french VARCHAR(100) UNIQUE,
    name_german VARCHAR(100) UNIQUE,
    name_spanish VARCHAR(100) UNIQUE,
    name_portuguese VARCHAR(100) UNIQUE,
    creation_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    update_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);


CREATE TABLE IF NOT EXISTS lmgc_Countries (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    continent_id INT NOT NULL,
    description TEXT,
    notes TEXT,
    iso_alpha_2 CHAR(2) UNIQUE,
    iso_alpha_3 CHAR(3) UNIQUE,
    iso_numeric CHAR(3) UNIQUE,
    image_path VARCHAR(255),
    suspended BOOLEAN DEFAULT FALSE,
    name_italian VARCHAR(100) UNIQUE,
    name_english VARCHAR(100) UNIQUE,
    name_french VARCHAR(100) UNIQUE,
    name_german VARCHAR(100) UNIQUE,
    name_spanish VARCHAR(100) UNIQUE,
    name_portuguese VARCHAR(100) UNIQUE,
    creation_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    update_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    FOREIGN KEY (continent_id) REFERENCES lmgc_Continents(id)
);





